import React, {Component} from 'react';
import './Crypto.css';
import CryptoList from './CryptoList';
import axios from 'axios';

class Crypto extends Component {

    constructor (props) {
        super(props);

        this.state = {
            cryptoList: []
        }
    }

    componentDidMount() {
        this.getCryptoData();
    }

    getCryptoData = () => {
        axios.get('https://blockchain.info/pl/ticker')
            .then(res => {
                const tickers = res.data;

                this.setState((state) => {
                    let newCryptoList = [];

                    for(const [ticker, cryptoRate] of Object.entries(tickers)){

                        let lastCryptoObject = state.cryptoList.find( (cryptoObject) => {
                            return(cryptoObject.currency === ticker)
                        });

                        console.log(lastCryptoObject);

                        let newCryptoObject = {
                            currency: ticker,
                            symbol: cryptoRate.symbol,
                            buy: cryptoRate.buy,
                            sell: cryptoRate.sell,
                            last: cryptoRate.last
                        }
                        newCryptoList.push(newCryptoObject);
                    }

                    return({
                        cryptoList: newCryptoList
                    })
                });
                console.log(newCryptoList);

            });
    }

    render() {
        return(
            <CryptoList CryptoList={this.state.cryptoList}/>
        )
    }
}

export default Crypto;
